func MakeTestBlockContext(mc *chain.ClientMock, blockNumber uint64) *blockContext {
	st := storage.NewStorage("")
	bc := MakeBlockContext(st, mc)
	bc.block = &models.Pbft{}
	bc.block.Number = blockNumber
	bc.block.TransactionCount = 1

	return bc
}

